import React, { Component, useEffect, useState } from "react";
import BootStrapTable from "react-bootstrap-table-next";
import axios from "axios";
import paginationFactory from "react-bootstrap-table2-paginator";
import { Modal, Button } from "react-bootstrap";

const TableDisplay = () => {
  const [journals, setJournals] = useState([]);

  const [journalinfo, setJournalInfo] = useState([]);
  const [showJournal, setshowJournal] = useState([]);

  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const getJournalData = async () => {
    try {
      const data = await axios.get("http://localhost:5000/journal");
      // console.log(data.data);
      setJournals(data.data);
    } catch (e) {
      //  console.log(e);
    }
  };
  useEffect(() => {
    getJournalData();
  }, []);

  const columns = [
    { dataField: "title", text: "Title" },
    { dataField: "date", text: "Date" }
  ];

  const rowEvents = {
    onClick: (e, row) => {
      console.log(row);
      setJournalInfo(row);
      toggleTrueFalse();
    }
  };

  const toggleTrueFalse = () => {
    setshowJournal(handleShow);
  };

  const JournalContent = () => {
    return (
      <Modal show={show} onHide={handleClose}>
        <Modal.Header>
          <Modal.Title>{journalinfo.title}</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <p>{journalinfo.description}</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary">Edit</Button>
          <Button variant="danger">Delete</Button>
        </Modal.Footer>
      </Modal>
    );
  };

  return (
    <div className="App">
      <h3>Journal Entries</h3>
      <BootStrapTable
        keyField="_id"
        data={journals}
        columns={columns}
        pagination={paginationFactory()}
        rowEvents={rowEvents}
      />

      {show ? <JournalContent /> : null}
    </div>
  );
};

export default class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      journals: []
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/journal")
      .then(response => {
        this.setState({ journals: response.data });
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    return <TableDisplay />;
  }
}
