import React, { Component, useState } from "react";
import axios from "axios";
import { Form, Button } from "react-bootstrap";
import "./create.component.css";
export default class Create extends Component {
  constructor(props) {
    super(props);

    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangeDate = this.onChangeDate.bind(this);

    this.onSubmit = this.onSubmit.bind(this);
    this.state = {
      title: "",
      description: "",
      date: new Date()
    };

    const [validated, setValidated] = useState(false);
  }
  handleSubmit = event => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }

    setValidated(true);
  };

  onChangeTitle(e) {
    this.setState({ title: e.target.value });
  }

  onChangeDescription(e) {
    this.setState({ description: e.target.value });
  }

  onChangeDate(e) {
    this.setState({ date: e.target.value });
  }
  onSubmit(e) {
    e.preventDefault();

    const entry = {
      title: this.state.title,
      description: this.state.description,
      date: this.state.date
    };
    console.log(entry);

    axios
      .post("http://localhost:5000/journal/add", entry)
      .then(res => console.log(res.data));

    this.setState({
      title: "",
      description: "",
      date: new Date()
    });

    document.getElementById("form").reset();
  }

  getValidationState() {
    const length = this.state.value.length;
    if (length > 10) return "success";
    if (length > 5) return "warning";
    if (length > 0) return "error";
    return null;
  }

  render() {
    return (
      <Form
        validationState={this.getValidationState()}
        id="form"
        className="form"
      >
        <Form.Group id="formTitle">
          <Form.Label>Title</Form.Label>
          <Form.Control
            type="text"
            placeholder="Everything has a title"
            onChange={this.onChangeTitle}
            required
          />
        </Form.Group>

        <Form.Group id="formDescription">
          <Form.Label>Description </Form.Label>
          <Form.Control
            as="textarea"
            rows={5}
            placeholder="Express yourself"
            onChange={this.onChangeDescription}
            required
          />
        </Form.Group>

        <Form.Group id="formDate">
          <Form.Label>Date</Form.Label>
          <Form.Control
            type="date"
            onChange={this.onChangeDate}
            required
          ></Form.Control>
        </Form.Group>

        <Button variant="primary" type="submit" onClick={this.onSubmit}>
          Submit
        </Button>
      </Form>
    );
  }
}
