import React, { Component, useState } from "react";
import axios from "axios";
import { Form, Button, Col, InputGroup } from "react-bootstrap";
import "./create.component.css";

export default class UpdateExercise extends Component {
  constructor(props) {
    super(props);

    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangeDate = this.onChangeDate.bind(this);

    this.onSubmit = this.onSubmit.bind(this);
    this.state = {
      title: "",
      description: "",
      date: new Date()
    };
  }

  componentDidMount() {
    console.log(this.props.id);
    axios
      .get("http://localhost:5000/journal/" + this.props.match.params.id)
      .then(res => {
        this.setState({
          title: res.data.title,
          description: res.data.description,
          date: res.data.date
        });
      })
      .catch(res => "Error");
  }

  onChangeTitle(e) {
    this.setState({ title: e.target.value });
  }

  onChangeDescription(e) {
    this.setState({ description: e.target.value });
  }

  onChangeDate(e) {
    this.setState({ date: e.target.value });
  }
  onSubmit(e) {
    e.preventDefault();

    const entry = {
      title: this.state.title,
      description: this.state.description,
      date: this.state.date
    };

    console.log(entry);

    axios
      .post(
        "http://localhost:5000/journal/update" + this.props.match.params.id,
        entry
      )
      .then(res => console.log("Post success"))
      .catch(res => "Error");

    if (
      this.state.title !== "" &&
      this.state.description !== "" &&
      this.state.date !== null
    ) {
      document.getElementById("form").reset();

      this.setState({
        title: "",
        description: "",
        date: new Date()
      });
    }
  }

  render() {
    return (
      <Form validated id="form" className="form">
        <Form.Group id="formTitle">
          <Form.Label>Title</Form.Label>
          <Form.Control
            type="text"
            text={this.state.title}
            onChange={this.onChangeTitle}
            required
          />
          <Form.Control.Feedback type="invalid">
            Please provide title
          </Form.Control.Feedback>
        </Form.Group>

        <Form.Group id="formDescription">
          <Form.Label>Description </Form.Label>
          <Form.Control
            as="textarea"
            rows={5}
            placeholder="Express yourself"
            onChange={this.onChangeDescription}
            required
          />
          <Form.Control.Feedback type="invalid">
            Please provide a description
          </Form.Control.Feedback>
        </Form.Group>

        <Form.Group id="formDate">
          <Form.Label>Date</Form.Label>
          <Form.Control type="date" onChange={this.onChangeDate} required />
        </Form.Group>

        <Button variant="primary" type="submit" onClick={this.onSubmit}>
          Submit
        </Button>
      </Form>
    );
  }
}
